TriggerGroup.script:
--This should always be disabled,
--it is activated by the timer CompleteCurrentQuests.

--cecho("\n<:red><black>updateQuests Trigger\n")

questing.QuestsCompleted = {}
questing.QuestsIncomplete = {}

Trigger.script:
--cecho("\n<:red><black>updateQuestsTitle Trigger\n")
questing.QuestGoals = {}
questing.Questmaster = matches[3] questing.QuestTitle = matches[2]

--cecho("\n<:red><black>updateQuestsGoals Trigger\n")
if matches[2] == " " then --cecho(" <:red><black>This goal is incomplete") table.insert(questing.QuestGoals, false)
elseif matches[2] == "X" then --cecho(" <:red><black>This goal is complete") table.insert(questing.QuestGoals, true) end

Alias.script:
ncom("Available prompts: Noax, Marcho, Obsc, Newbie, All, and Creation")
echo("\n")
cecho("\n<DarkSlateGrey>           Syntax: <white>UIPrompt \<PromptName\>")

