function UI.CommsClick(tab) UI[UI.CurrentComms]:hide() UI.CurrentComms = tab for k, v in pairs(UI.CommsTabs) do UI[v .. "tab"]:setStyleSheet(UI.TabStyleSheet)
end
if tostring(tab) == "Who" then gagmode = true send("who",false) tempTimer(1.5,[[gagmode = false]])
end
UI[UI.CurrentComms]:show() disableTimer(UI.CurrentComms.."Flash") UI[UI.CurrentComms.."tab"]:setStyleSheet(UI.ActiveTabStyleSheet)
end

function UI.UIFeed()
  UI.FeedList =
    Geyser.MiniConsole:new(
      {
        name = "UI.FeedList",
        x = "2.5%",
        y = "2.5%",
        width = "95%",
        height = "80%",
        color = "black",
        scrollBar = false,
        autoWrap = true,
        font = UI.theme.Font,
        fontSize = tonumber(UI.theme.FontSize) - 2,
      },
      UI.Feed
    )
  UI.FeedDetails =
    Geyser.Label:new(
      {name = "UI.FeedDetails", x = "2.5%", y = "85%", width = "95%", height = "10%"}, UI.Feed
    )
  UI.FeedDetails:setStyleSheet([[background-color: black;
	]])
	--  UI.FeedDetails:setStyleSheet([[background-color: rgba(0,0,0,0%);
	--]])
end

