ncom("Available themes: Xaos, Reldi, Noax, Gobs, Solious, Blue, Green, Purple,")
cecho("\n<DarkSlateGrey>           Turquoise, White, Black, Grey, Random and Custom.")
echo("\n")
cecho("\n<DarkSlateGrey>           Syntax: <white>UITheme \<ThemeName\>")
echo("\n")
cecho("\n<DarkSlateGrey>           Current: <"..UI.theme.Color1..">"..UI.theme.Color1.." <"..UI.theme.Color5..">"..UI.theme.Color5.." <"..UI.theme.Color6..">"..UI.theme.Color6)

ncom("Available prompts: Noax, Marcho, Obsc, Newbie, All, and Creation")
echo("\n")
cecho("\n<DarkSlateGrey>           Syntax: <white>UIPrompt \<PromptName\>")

local reset = matches[11]
if string.lower(reset) == "reset" then UI.Session = getTime()
if tostring(UI.CurrentExtras) == "Feed" then UI.UpdateLabelFeed()
end
else ncom("Syntax: <white>UISession\<reset\>")
end

if scripter.On == true then ncom("scripter is already <white>ON")
else UI.scripter.On = true scripter.On = true ncom("scripter is now <white>ON")
end
if tostring(UI.CurrentExtras) == "Scripter" then UI.UpdateLabelScripter()
end

if scripter.On == false then ncom("scripter is already <white>Off")
else UI.scripter.On = false scripter.On = false ncom("scripter is now <white>Off")
end
if tostring(UI.CurrentExtras) == "Scripter" then UI.UpdateLabelScripter()
end

local arg = matches[14]
if string.lower(arg) == "on" then scripter.Kill = true UI.scripter.Kill = true ncom("Scripters Kill mode is now <white>On")
elseif string.lower(arg) == "off" then scripter.Kill = false UI.scripter.Kill = false ncom("Scripters Kill mode is now <white>Off")
else ncom("Syntax: <white>scripterKill \<On|Off\>")
end
if tostring(UI.CurrentExtras) == "Scripter" then UI.UpdateLabelScripter()
end

local arg = matches[16]
if string.lower(arg) == "auto" then enableTrigger("wield detection")
else disableTrigger("wield detection") scripter.Disarm = arg UI.scripter.Disarm = arg
end
if tostring(UI.CurrentExtras) == "Scripter" then UI.UpdateLabelScripter()
end

if questing.On == true then ncom("Now You're Questing With Noax is already <white>On")
else questing.On = true enableTimer("questingWithNoax") enableTrigger("questingWithNoax") ncom("Now You're Questing With Noax is now <white>On")
end
if tostring(UI.CurrentExtras) == "Questing" then --ScripterLabel Update
end

if questing.On == false then ncom("Now You're Questing With Noax is already <white>Off")
else questing.On = false disableTimer("questingWithNoax") disableTrigger("questingWithNoax") ncom("Now You're Questing With Noax is now <white>Off")
end
if tostring(UI.CurrentExtras) == "Questing" then --ScripterLabel Update
end

