function UI.CommsClick(tab)

	if tostring(tab) == "Who" then
		gagmode = true
		send("who",false)
		tempTimer(1.5,[[gagmode = false]])

	end

	UI[UI.CurrentComms]:hide()
	UI.CurrentComms = tab
	for i = 1, #UI.CommsTabs do
		UI[UI.CommsTabs[i].."tab"]:setStyleSheet(
  	[[background-color: rgba(45,45,45,100%);
			border-top-left-radius: ]] .. UI.theme.BorderRadius .. [[px;
			border-top-right-radius: ]] .. UI.theme.BorderRadius .. [[px;
    	border-width: ]] .. UI.theme.BorderWidth .. [[px;
    	border-top-color: ]] .. UI.theme.Color5 .. [[;
			border-left-color: ]] .. UI.theme.Color5 .. [[;
			border-right-color:  ]] .. UI.theme.Color5 .. [[;
			border-bottom-color: Black;
    	border-style: ]] .. UI.theme.BorderStyle .. [[;
    	padding: 0px;
    	margin: 0.5%;
		]])

	end

	UI[UI.CurrentComms]:show()
	disableTimer(UI.CurrentComms.."Flash")
	UI[UI.CurrentComms.."tab"]:setStyleSheet(
  	[[background-color: Black;
			border-top-left-radius: ]] .. UI.theme.BorderRadius .. [[px;
			border-top-right-radius: ]] .. UI.theme.BorderRadius .. [[px;
    	border-width: ]] .. UI.theme.BorderWidth .. [[px;
    	border-top-color: ]] .. UI.theme.Color5 .. [[;
			border-left-color: ]] .. UI.theme.Color5 .. [[;
			border-right-color:  ]] .. UI.theme.Color5 .. [[;
			border-bottom-color: Black;
    	border-style: ]] .. UI.theme.BorderStyle .. [[;
    	padding: 0px;
    	margin: 0.5%;
	]])

end

function UI.UIComms()

	UI.CommsTabs = {"Room","Group","OOC","IC","Tell","Who",}

	UI.CurrentComms = UI.CurrentComms or UI.CommsTabs[1]

	UI.CommsHeader = Geyser.HBox:new({
  	name = "UI.CommsHeader",
		x = "2%",
		y = "2%",
		height = "5%",
		width = "97%",
	},UI.RTop)

	UI.CommsFooter = Geyser.Container:new({
		name = "UI.CommsFooter",
		x = "1%",
		y = "6.5%",
		height = "94%",
		width = "98%",
	},UI.RTop)

	for k,v in pairs(UI.CommsTabs) do

		UI[v.."tab"] = Geyser.Label:new({
			name = "UI."..v.."tab",
		},UI.CommsHeader)
		UI[v.."tab"]:setStyleSheet(
  	[[background-color: rgba(45,45,45,100%);
			border-top-left-radius: ]] .. UI.theme.BorderRadius .. [[px;
			border-top-right-radius: ]] .. UI.theme.BorderRadius .. [[px;
    	border-width: ]] .. UI.theme.BorderWidth .. [[px;
    	border-top-color: ]] .. UI.theme.Color5 .. [[;
			border-left-color: ]] .. UI.theme.Color5 .. [[;
			border-right-color:  ]] .. UI.theme.Color5 .. [[;
			border-bottom-color: Black;
    	border-style: ]] .. UI.theme.BorderStyle .. [[;
			margin-top: 3px;
			margin-right: 3px;
