function questing.Toggle()
if questing.toggle == true then questing.ToggleButton:echo("Enable", UI.theme.FontColor, "c" .. UI.theme.FontSize) questing.toggle = false disableTrigger("questingWithNoax") disableTimer("questingWithNoax") ncom("You're no longer Questing with Noax...")
elseif questing.toggle == false then questing.ToggleButton:echo("Disable", UI.theme.FontColor, "c" .. UI.theme.FontSize) questing.toggle = true enableTrigger("questingWithNoax") enableTimer("questingWithNoax") ncom("Now You're Questing with Noax!")
end
end
